      if (index === -1) {
        this.appendChild(newChild);
        return;
      }
      index++;
      if (index >= this.children.length) {
        this.appendChild(newChild);
        return;
      }
      const refWidget = this.children[index];
      this.children.splice(index, 0, newChild);
      this._element.insertBefore(newChild._element, refWidget._element);
