      if (cssValue.endsWith('%')) {
        cssValue = cssValue.substring(0, cssValue.length - 1);
      } else {
        cssValue = cssValue.substring(0, cssValue.length - 2);
      }
      if (cssValue.includes('.')) {
        return parseFloat(cssValue);
      }
      return parseInt(cssValue);
